import List from "../../components/list/List"
import { useEffect, useState } from "react";
import axios from "axios";
export default function Home({type}) {
  const [lists, setLists] = useState([]);
  const [genre, setGenre] = useState([]);

  useEffect(()=>{
    const getRandomeLists =async()=>{
      try {
        if(type && genre){
           const res = await axios.get(
          `/lists/ "?type="+type:""}${genre ? "?genre="+genre :""}`,{ headers:{
          token:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNDkwNTc5MDMyYzRkZTI1ODA1OWEzYiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzMjIzODA1MywiZXhwIjoxNjMyNDEwODUzfQ.pPkG09OWOAzKBiifNIaaTmppWm_GOH2oK456O0FylS0"
        }});

      console.log(res.data)
        setLists(res.data)
        }else{
         const res = await axios.get(
          `/lists/${type? "?type="+type:""}${genre ? "?genre="+genre :""}`,{ headers:{
          token:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNDkwNTc5MDMyYzRkZTI1ODA1OWEzYiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzMjIzODA1MywiZXhwIjoxNjMyNDEwODUzfQ.pPkG09OWOAzKBiifNIaaTmppWm_GOH2oK456O0FylS0"
        }});

      console.log(res.data)
        setLists(res.data)
      }
      } catch (error) {
        console.log(error)
      }

    }
    getRandomeLists();

  },[type,genre]);
    return (
        <div className="home">
          <Navbar/>
        <Featured type={type}/>

        {lists.map((list)=>(

          <List list={list}/> 

        ))}

        </div>
    )
}
