import { Add, PlayArrow, ThumbDownAltOutlined, ThumbUpAltOutlined } from "@material-ui/icons"
import axios from "axios";
import { useEffect, useState } from "react"
import "./listItem.scss"

export default function ListItem({ index, item}) {
    const [isHoverd, setIsHoderd] = useState(false);
    const [movie, setIsMovie] = useState();
    // const trailer =
    // "https://player.vimeo.com/external/371433846.sd.mp4?s=236da2f3c0fd273d2c6d9a064f3ae35579b2bbdf&profile_id=139&oauth2_token_id=57447761";

    useEffect(()=>{
        const getMovie = async()=>{
            try {
                const res = await axios.get("/movies/find/"+item ,{ headers:{
          token:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNDkwNTc5MDMyYzRkZTI1ODA1OWEzYiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzMjIzODA1MywiZXhwIjoxNjMyNDEwODUzfQ.pPkG09OWOAzKBiifNIaaTmppWm_GOH2oK456O0FylS0"
        }})
            } catch (error) {
                console.log(error)
            }
        }
    })
    return (
        <div className="listItem" onMouseEnter={()=>setIsHoderd(true)} onMouseLeave={()=> setIsHoderd(false)} style={{left: isHoverd && index*225 -50 + index*2.5}}>
            <img src={item.img} alt="" />
            {isHoverd && (
                <>
            <video src={item.trailer} autoPlay={true} loop/>
            <div className="iteminfo">
                <div className="icons">
                    <PlayArrow className="icon"/>
                    <Add className="icon"/>
                    <ThumbUpAltOutlined className="icon"/>
                    <ThumbDownAltOutlined className="icon"/>
                </div>
                <div className="itemInfoTop">
                    <span>{item.duration}</span>
                    <span className="limit">{item.limit}+</span>
                    <span>{item.year}</span>
                </div>
                <div className="desc">
                    {item.desc}
                </div>
                <div className="genre">{item.genre}</div>
            </div>
           </> 
           )}
        </div>
    );
}
