import "./app.scss"
import Home from "./pages/home/Home";
import Register from "./pages/register/Register";
import Watch from "./pages/watch/Watch";
import Login from "./pages/login/Login";
import UserList from "./pages/userList/UserList";
import NewUser from "./pages/newUser/NewUser";
import ProductList from "./pages/productList/ProductList";
import NewProduct from "./pages/newProduct/NewProduct";
import Product from "./pages/product/Product";
import User from "./pages/user/User";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";
import HomeAdmin from "./pages/homeAdmin/HomeAdmin";
import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const MONTHS = [
   'Jan',
    'Feb',
   'Mar',
    'Apr',
   'May',
   'Jun',
   'Jul',
    'Aug',
   'Sep',
   'Oct',
  'Nov', 
  'Dec'
  ]
  const [userStats, setUserStats]= useState([]);
  useEffect(()=>{
    const getStats = async()=>{
      try {
        const res = await axios.get("/users/stats", {
          headers: {
            token:
              "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNDkwNTc5MDMyYzRkZTI1ODA1OWEzYiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzMjE4MTk3NywiZXhwIjoxNjMyMzU0Nzc3fQ.pdPrUx9woUIaoucGnxdctb4BPKH8hhEQFsPhR8wmo_U"
          },
        });
        setUserStats(res.data);
      } catch (error) {
        console.log(error)
      }
    }
    getStats();
  },[]);
  const user = true;
  return (
    <div className="App">
      <Router>
      <Switch>
         <Route exact path="/">
          {user? <Home/>: <Redirect to="/register"/>}
        </Route>

         <Route  path="/movies">
       { user?  <Home type="movie"/>:<Redirect to="/register"/> }
        </Route>
        <Route  path="/series">
         {user? <Home type="series"/> :<Redirect to="/register"/>}
        </Route>
        <Route  path="/watch">
         { user? <Watch/>:<Redirect to="/register"/>}
        </Route>
        <Route  path="/register">
         { !user? <Register/>: <Redirect to="/"/>}
        </Route>
         <Route  path="/login">
        { !user? <Login/> :<Redirect to="/"/>}
        </Route>
        <Route  path="/admin">
        { user? <HomeAdmin/> :<Redirect to="/"/>}
        </Route>
        <Route path="/users">
            <UserList/>
          </Route>
           <Route path="/newuser">
            <NewUser/>
          </Route>
           <Route path="/products">
            <ProductList/>
          </Route>
          <Route path="/newproduct">
            <NewProduct/>
          </Route>
          <Route path="/product/:productId">
            <Product/>
          </Route>
          <Route path="/user/:userId">
            <User/>
          </Route>
      </Switch>
    </Router>


    </div>
  );
}

export default App;
