import "./list.scss"
import {ArrowBackIosOutlined, ArrowForwardIosOutlined} from "@material-ui/icons"
import ListItem from "../listItem/ListItem"
import { useRef, useState } from "react"
export default function List() {
    const [slideNumber, setSlideNumber] = useState(0);

    const listRef = useRef();

    const handleClik = (direction)=>{
        let distance = listRef.current.getBoundingClientRect().x - 56;
        if(direction === "left" && slideNumber >0){
            setSlideNumber(slideNumber -1);
            listRef.current.style.transform=`translateX(${230 + distance }px)`
        }
          if(direction === "right" && slideNumber<5 ){
              setSlideNumber(slideNumber+ 1)
            listRef.current.style.transform=`translateX(${-230 - distance }px)`
        }
    }
    return (
        <div className="list">
            <span className="listTitle">Continue to watch</span>
            <div className="wrapper">
                <ArrowBackIosOutlined className="sliderArrow left" onClick={()=>handleClik("left")} />
                <div className="container " ref={listRef}>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                    <ListItem/>
                </div>
                <ArrowForwardIosOutlined className="sliderArrow right" onClick={()=>handleClik("right")}/>
            </div>
        </div>
    )
}
