import { InfoOutlined, PlayArrow } from "@material-ui/icons"
import axios from "axios";
import { useEffect, useState } from "react"
import "./featured.scss"

export default function Featured({type}) {
    const [content, setContent] = useState(null);
    useEffect(()=>{
        const getContent = async()=>{
            try {
                const res = await axios.get("/movies/random",{ headers:{
          token:"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNDkwNTc5MDMyYzRkZTI1ODA1OWEzYiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzMjIzODA1MywiZXhwIjoxNjMyNDEwODUzfQ.pPkG09OWOAzKBiifNIaaTmppWm_GOH2oK456O0FylS0"
        }})
        setContent
            } catch (error) {
                console.log(error)
            }
        }
    })
    return (
        <div className="featured">
            {type && (
                <div className="category">
                    <span>{type==="movie"? "Movies": "TV Series"}
                    <select name="genre" id="genre">
                            <option >Genre</option>
                            <option value="comedy">Comedy</option>
                            <option value="crime">Crime</option>
                            <option value="fantasy">Fantasy</option>
                            <option value="historical">Historical</option>
                            <option value="horror">Horror</option>
                            <option value="romance">Romance</option>
                            <option value="sci-fi">Sci-fi</option>
                            <option value="thriller">Thriller</option>
                            <option value="western">Western</option>
                            <option value="animation">Animation</option>
                            <option value="drama">Drama</option>
                            <option value="documentary">Documentary</option>

                    </select>
                    </span>
                </div>
            )}
            <img src="https://images.unsplash.com/photo-1524985069026-dd778a71c7b4?ixid=MnwxMjA3fDB8MHxzZWFyY2h8N3x8bW92aWVzfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=60" alt="" />
            <div className="info">
                <img src="https://images.unsplash.com/photo-1616530940355-351fabd9524b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8M3x8bW92aWVzfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=60" alt="" />
                <span className="desc">
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Magnam cum dignissimos obcaecati facere odio magni veritatis sint eius officia ad a, illum fugiat minima dicta, quibusdam quidem vero ipsa quasi.
                </span>
                <div className="buttons">
                    <button className="play">
                        <PlayArrow/>
                        <span>Play</span>
                    </button>
                    <button className="more">
                        <InfoOutlined/>
                        <span>Info</span>
                    </button>
                </div>
            </div>

        </div>
    )
}
